document.addEventListener('DOMContentLoaded', function() {
    const swiper = new Swiper('.hero-section.swiper', {
        loop: true,
        pagination: {
            el: '.swiper-pagination',
            clickable: true,
        },
        navigation: {
            nextEl: '.swiper-button-next',
            prevEl: '.swiper-button-prev',
        },
        autoplay: {
            delay: 5000,
            disableOnInteraction: false,
        },
        effect: 'fade',
        fadeEffect: {
            crossFade: true
        },
    });

    console.log('Swiper initialized:', swiper);
});

document.addEventListener('DOMContentLoaded', function() {
    document.addEventListener('DOMContentLoaded', function() {
        const productCarousel = document.querySelector('#product-carousel');
        const gifImage = productCarousel.querySelector('img[src="assets/img/Shop-single/Celine.gif"]');
    
        let currentIndex = 0;
        let currentDuration = 4000; // Default speed for other images
    
        function goToSlide(index) {
            // ... your code to switch to the specified slide ...
        }
    
        function startCarousel() {
            setInterval(() => {
                goToSlide(currentIndex + 1);
                currentIndex = (currentIndex + 1) % productCarousel.querySelectorAll('.carousel-item').length; // Loop through slides
                currentDuration = (gifImage.src === productCarousel.querySelector('.carousel-item.active img').src) ? 10000 : 4000; // Change duration for GIF
            }, currentDuration);
        }
    
        startCarousel();
    });
    console.log('Swiper initialized:', productCarousel);
});